    import React, { useEffect, useState } from 'react'
    import '../products.json'
    import { useParams } from 'react-router-dom';
    import PageHeader from '../components/PageHeader';
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import { Autoplay } from 'swiper/modules';
import ProductDisplay from './ProductDisplay';



    const SingleProduct = () => {
    const  [product , setproduct] = useState([]);
    const { id } = useParams() ;
    useEffect(() => {
        fetch("/src/products.json").then (res => res.json()).then(data => setproduct(data))
    }, [])

    const result = product.filter((p) => p.id === id);
    
    return (
    <div>
        <PageHeader title={"OUR SHOP SINGLE"} curPage={"Shop / Single Product "} />

        <div className='shop-single padding-tb aside-bg'>
            <div className='container'>
                <div className="row justify-contenet-center">
                    <div className='col-lg-8 col-12'>
                    <article>
                        <div className='product-details'>
                            <div className='row align-items-center'>
                            <div className='col-md-6 col-12'>
                                <div className='product-thumb'>
                                    <div className='swiper-container pro-single-top'>
                                    <Swiper
                                    spaceBetween={30}
                                    slidesPerView={1}
                                    loop={"true"}
                                    autoplay={{
                                        delay: 2000,
                                        disableOnInteraction: false
                                    }}
                                    modules={[Autoplay]}
                                    navigation = {
                                        {
                                            prevEl:".pro-single-prev",
                                            nextvEl:".pro-single-next",
                                        }
                                    }
                                    className="mySwiper">
                                    {
                                        result.map((item, i) => (
                                            <SwiperSlide key={i}>
                                                <div className='single-thumb'>
                                                    <img src={item.img} alt='' />
                                                    </div>
                                            </SwiperSlide>

                                        ))
                                    }
                                    </Swiper>
                                    <div className='pro-single-next'>
                                        <i className='iconfont-rounded-left'></i>
                                    </div>
                                    <div className='pro-single-prev'>
                                        <i className='iconfont-rounded-right'></i>
                                    </div>
                                    </div>
                                </div>
                            </div>
                            <div className='col-md-6 col-12'>
                                <div className='post-content'>
                                    <div>
                                        {
                                            result.map(item => <ProductDisplay key={item.id} item={item} /> )
                                        }
                                    </div>
                                </div>
                            </div>
                            </div>
                        </div>
                        {/* reviews */}
                        <div className='review'>
                            Review
                        </div>
                    </article>
                    </ div>
                        {/* Right Side */}
                    <div className='col-lg-4 col-12'>Right Side</div>
            </div>
        </div>
    </div>
</div>
    )
    }
    export default SingleProduct;
