import React, { useState } from 'react'
import PageHeader from '../components/PageHeader'
import Data from "../products.json"
import ProductCards from './ProductCards'
import Pgaination from './Pagination'
const showResults = "Showing 01 - 12 of 139 Results"

// pagination
const [currentPage, setCurrentPage] = useState(1);
const productsPerPage = 12;
const indexOfLastProduct = currentPage * productsPerPage;
const indexOfFirstProduct = currentPage - productsPerPage;
const currentProducta = product.slice(indexOfLastProduct , indexOfFirstProduct)

// function to changenthe current page 
const paginate = (pageNumber) => {
  setCurrentPage
}

const Shop = () => {
const [GridList, setGridList] = useState(true);
const [products, setproducts] = useState(Data);


  return (
    <div>
      <PageHeader title="Our Shop Page" curPage="Shop" />
      {/* shop page */}
      <div className="shop-page paddig-tb">
        <div className="container">
          <div className="row-justify-content-center">
            <div className="col-lg-8 col-12">
              <article>
                {/* layout and title here */}
                <div className="shop-title d-flex flex-warp justify-content-between">
            <p>
              {showResults}
            </p>
            <div className={`product-view-mode ${GridList ? "gridActive" : "listActive"}`}>
            <a className="grid" onClick={() => setGridList(!GridList)}>
              <i className='icofont-ghost'></i>
            </a>
            <a className="list" onClick={() => setGridList(!GridList)}>
              <i className='icofont-listine-dots'></i>
            </a>
            </div>
                </div>
                {/* products card */}
                <div>
                <ProductCards GridList={GridList} products={products} />
                </div>
                <Pgaination
                
                productsPerPage={productsPerPage}
                
                />
              </article>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Shop
